<?phprequire_once 'config.php';//require_once 'https://aegee-bucuresti.eu/clubsportiv/assets/functions/emailsender.php';require_once 'emailsender.php';function send_survey(){    if ($_SERVER['REQUEST_METHOD'] == 'POST' && isset($_POST['survey'])) {        $option = $_POST['send-survey'];        $email = $_POST['email_survey'];        $code = time();        global $con;        $sql = "insert into survey (antrenor, email, code) values ('$option','$email','$code')";        $result = mysqli_query($con, $sql);        if(sendemail_survey($email, $option, $code)){            echo '<div class="alert alert-success">Formularul a fost trimis cu success</div>';        }//        if ($result) {////            echo '<div class="alert alert-success">Formularul a fost trimis cu success</div>';//        }else{//            echo ''//        }    }}function changepass(){    if ($_SERVER['REQUEST_METHOD'] == 'POST' && isset($_POST['changepass'])) {        $oldpass = $_POST['oldpass'];        $newpass = $_POST['newpass'];        $newpass2 = $_POST['newpass2'];        if ($newpass == $newpass2) {            global $con;            $query = "update accounts set password='$newpass' where password='$oldpass'";            if (!mysqli_query($con, $query)) {                echo '<div class="alert alert-danger">Parola actuala este incorecta</div>';            }        } else {            echo '<div class="alert alert-danger">Parolele nu se potrivesc</div>';        }    }}function rezerv(){    if ($_SERVER['REQUEST_METHOD'] == 'POST' && isset($_POST['btn_rezerv'])) {        if (isset($_POST['h-captcha-response']) && !empty($_POST['h-captcha-response'])) {            $data = array(                'secret' => "0xCDcfBFAB89B92474D09e0af0ECE2b63986993484",                'response' => $_POST['h-captcha-response']            );            $verify = curl_init();            curl_setopt($verify, CURLOPT_URL, "https://hcaptcha.com/siteverify");            curl_setopt($verify, CURLOPT_POST, true);            curl_setopt($verify, CURLOPT_POSTFIELDS, http_build_query($data));            curl_setopt($verify, CURLOPT_RETURNTRANSFER, true);            $verifyResponse = curl_exec($verify);            $responseData = json_decode($verifyResponse);            if ($responseData->success) {                $LName = $_POST['name'];                $FName = $_POST['prenume'];                $tel = $_POST['phone'];                $email = $_POST['email'];                $data = $_POST['date'];                $time = $_POST['time'];                $msj = $_POST['message'];                include 'db.php';                global $con;                $sql = "insert into rezervari (nume,prenume,tel,email,zi,ora,extra) values ('$LName','$FName','$tel','$email','$data','$time','$msj')";                $result = mysqli_query($con, $sql);                if ($result) {                    sendrezerv($email, $LName, $data, $time);                    header('Location:index.php');                }            } else {                echo '<div class="alert alert-danger">Va rugam incercati mai tarziu</div>';            }        } else {            echo '<div class="alert alert-danger">Trebuie sa arati ca nu esti un robot</div>';        }        echo '<div class="alert alert-success">Formularul a fost trimis cu success</div>';    }}function login_validation(){    if ($_SERVER['REQUEST_METHOD'] == 'POST' && isset($_POST['login'])) {        include 'db.php';        global $con;        $user = mysqli_real_escape_string($con, $_POST['user']);        $pass = mysqli_real_escape_string($con, $_POST['pass']);        if (login($user, $pass)) {            header("location:index.php");            // $_SERVER['REMOTE_ADDR'];        } else {            echo '<div class="alert alert-danger">Datele de autentificare nu sunt corecte</div>';        }    }}function login($user, $pass){    global $con;    $query = "select * from accounts where user='$user'";    $result = mysqli_query($con, $query);    if ($row = mysqli_fetch_assoc($result)) {        $db = $row['password'];        if ($pass == $db) {            setcookie('user', $user, time() + 86400);            $_SESSION['user'] = $user;            if ($row['admin'] == 1) {                $_SESSION['admin'] = 1;            }            return true;        } else {            return false;        }    } else {        return false;    }}function dictionary($name){    global $con;    $query = "select * from dictionary where name='$name'";    $result = mysqli_query($con, $query);    if ($row = mysqli_fetch_assoc($result)) {        return $row['value'];    } else {        return "Nu s-a gasit elementul respectiv";    }}function input_dictionary($tab){    echo '<form method="post">';    echo '<div class="form-group">';    echo '    <div class="row">';    echo '        <div class="col-8">';    echo '          <input style="float:left!important;" class="form-control" name="' . $tab . '" type="text" value="' . dictionary($tab) . '">';    echo '        </div>';    echo '        <div class="col-4">';    echo '            <button style="float:right!important;" type="submit" class="btn btn-primary">Modifica</button>';    echo '        </div>';    echo '    </div>';    echo '</div>';    echo '</form>';    if (isset($_POST[$tab])) {        global $con;        $newvalue = $_POST[$tab];        $query = "update dictionary set value='$newvalue' where name='$tab'";        $result = mysqli_query($con, $query);        if ($result) {            echo "Schimbarea a fost facuta cu succes!";        } else {            echo "Ceva nu a mers bine";        }    }}function upload_img($var){    global $con;    if (isset($_POST[$var])) {        $name = $_FILES['file']['name'];        $target_dir = "/home/clubvoin/public_html/admin/uploads/";        $target_file = $target_dir . basename($_FILES["file"]["name"]);        $imageFileType = strtolower(pathinfo($target_file, PATHINFO_EXTENSION));        $extensions_arr = array("jpg", "jpeg", "png", "gif");        if (in_array($imageFileType, $extensions_arr)) {            if (move_uploaded_file($_FILES['file']['tmp_name'], $target_dir . $name)) {                $query = "update dictionary set value='$name' where name='$var'";                mysqli_query($con, $query);            }            echo "<meta http-equiv='refresh' content='0'>";        }    }}function stats_emails(){    global $con;    $val = config_check('emails') + 1;    $query = "update configs set value='$val' where name='emails'";    mysqli_query($con, $query);}function config_content($var){    if ($_SERVER['REQUEST_METHOD'] == 'POST' && isset($_POST['btn_switch'])) {        $content_switch = $_POST['btn_switch'];        global $con;        $query = "update configs set value='$content_switch' where name='$var'";        $result = mysqli_query($con, $query);        if (!$result) {            echo "Ceva nu a mers bine";        }    }}function config_check($name){    global $con;    $query = "select * from configs where name='$name'";    $result = mysqli_query($con, $query);    if ($row = mysqli_fetch_assoc($result)) {        return $row['value'];    } else {        return "Nu s-a gasit elementul respectiv";    }}function rezerv_status($butt, $buttt){    if ($_SERVER['REQUEST_METHOD'] == 'POST' && isset($_POST[$butt])) {        global $con;        $query = "update rezervari set status='1' where id='$butt'";        $result = mysqli_query($con, $query);        if (!$result) {            echo "ceva nu a mers bine";        } else {            $query = "select * from rezervari where id='$butt'";            $result = mysqli_query($con, $query);            if ($row = mysqli_fetch_assoc($result)) {                $email = $row['email'];                $nume = $row['prenume'];                $time = $row['ora'];                $data = $row['zi'];            }            accrezv($email, $nume, $data, $time);        }        echo "<meta http-equiv='refresh' content='0'>";    }    if ($_SERVER['REQUEST_METHOD'] == 'POST' && isset($_POST[$buttt])) {        global $con;        $query = "update rezervari set status='2' where id='$butt'";        $result = mysqli_query($con, $query);        if (!$result) {            echo "ceva nu a mers bine";        } else {            $query = "select * from rezervari where id='$butt'";            $result = mysqli_query($con, $query);            if ($row = mysqli_fetch_assoc($result)) {                $email = $row['email'];                $nume = $row['prenume'];            }            refrezv($email, $nume, $data, $time);            echo "<meta http-equiv='refresh' content='0'>";        }    }}